import React from "react";
import { useState } from "react";
import "./ExpenseForm.css";

const ExpenseForm = () => {
  // const [enteredTitle, setEnteredTitle] = useState('');
  // const [enteredAmount, setEnteredAmount] = useState('');
  // const [enteredDate, setEnteredDate] = useState('');
  const [userInput, setUserInput] = useState({
    enteredTitle: '',
    enteredAmount: '',
    enteredDate: ''
  });

  const titleChangeHandeler = (event) =>{
    // setEnteredTitle(event.target.value);
    
    // setUserInput({
    //   ...userInput,
    //   enteredTitle: event.target.value
    // }) This is not a good Practice as it is not guaranteed to give the latest state value 
    // and it is dependig on the previous state value for updating the current State

    setUserInput((prevState)=>{
      return {...prevState, enteredTitle: event.targt.value}
    })

  }

  const amountChangeHandeler = (event) =>{
    // setEnteredAmount(event.target.value);
    // setUserInput({
    //   ...userInput,
    //   enteredAmount: event.target.value
    // })

    setUserInput((prevState) => {
      return {...prevState, enteredAmount: event.target.value}
    })
  }

  const dateChangeHandeler = (event) =>{
    // setEnteredDate(event.target.value);
    // setUserInput({
    //   ...userInput,
    //   enteredDate: event.target.value
    // })
    
    setUserInput((prevState) =>{
      return{...prevState, enteredDate: event.target.value}
    })
  }
  return (
    <form>
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>Title</label>
          <input type="text" onChange={titleChangeHandeler}/>
        </div>
        <div className="new-expense__control">
          <label>Amount</label>
          <input type="number" min="0.01" step="0.01" onChange={amountChangeHandeler}/>
        </div>
        <div className="new-expense__control">
          <label>Date</label>
          <input type="date" min="2019-01-01" max="2022-12-31" onChange={dateChangeHandeler}/>
        </div>
      </div>
      <div className="new-expense__actions">
        <button type="submit">Add Expense</button>
      </div>
    </form>
  );
};

export default ExpenseForm;
